(function($){'use strict';var Counter=function(element,options){var settings=$.extend({},$.fn.counter.tmcOpts,options);var el=$(element);var obj=this;var countTimer;obj.startCounter=function(){obj.clearCounter();var fromNumber=el.data('count-from')?parseFloat(el.data('count-from')):settings.from;var toNumber=el.data('count-to')?parseFloat(el.data('count-to')):settings.to;var countInterval=el.data('count-interval')?parseFloat(el.data('count-interval')):settings.interval;var direction=fromNumber<=toNumber?'up':'down';countTimer=setInterval(function(){if(fromNumber===toNumber||isNaN(fromNumber)||isNaN(toNumber)){clearInterval(countTimer);if(settings.onComplete)settings.onComplete()}
el.text(formatNumber(fromNumber));if(direction==='up')fromNumber++;else fromNumber--},countInterval)};obj.clearCounter=function(){clearInterval(countTimer);var initNumber=el.data('count-from')?parseFloat(el.data('count-from')):settings.from;el.html(initNumber)};function formatNumber(number){return number.toString().replace(/(\d)(?=(\d{3})+$)/g,'$1,')}
if(settings.autoStart)obj.startCounter()};$.fn.counter=function(options){return this.each(function(){var element=$(this);if(element.data('counter'))return;var counter=new Counter(this,options);element.data('counter',counter)})};$.fn.counter.tmcOpts={autoStart:!0,from:500,to:100,interval:20,onComplete:null}})(jQuery)